package praktikum_4;

public class forDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i < 11; i++) {
			System.out.println("for 1 " + i);
		}
		for (int j = 10; j >= -3; j--) {
			System.out.println("for 2 " + j);
		}
		for (int k = 10, g = 2; g >= -3 & k < 10; g++, k--) {
			System.out.println("for 87 :" + g + ' ' + k);
		}
	}

}
